mapper.select:
select

from 
	job_info
where 
	delete_flag = 1

	and job_name = #{jobName,jdbcType=VARCHAR} 


	and job_title like CONCAT('%' , '${jobTitle}' , '%' ) 


	and job_class = #{jobClass,jdbcType=VARCHAR} 


	and triger_type = #{trigerType , jdbcType=INTEGER} 


	and run_group_id = #{runGroupId,jdbcType=BIGINT}

select info.id as id, info.job_name as jobName, info.job_title as jobTitle, info.job_class as jobClass, info.job_triger as jobTriger, info.run_group_id as runGroupId, info.begin_time as beginTime, info.end_time as endTime, info.next_time as nextTime, info.pause as pause, info.lock_key as lockKey, info.expire_time as expireTime, info.time_out as timeOut, info.job_list as jobList, info.triger_type as trigerType, info.log_type as logType, info.remark as remark, info.create_time as createTime, info.create_user_id as createUserId, info.create_user_name as createUserName, info.update_time as updateTime, info.update_user_id as updateUserId, info.update_user_name as updateUserName, info.delete_flag as deleteFlag, g.group_name as groupName, g.ip as ip from job_info info left join job_group g on info.run_group_id = g.id where info.delete_flag = 1
and info.job_name = #{jobName,jdbcType=VARCHAR}
and info.job_title like CONCAT('%' , '${jobTitle}' , '%' )
and info.job_class = #{jobClass,jdbcType=VARCHAR}
and info.triger_type = #{trigerType , jdbcType=INTEGER}
and info.run_group_id = #{runGroupId,jdbcType=BIGINT}

select
from job_info where delete_flag = 1
and ( job_title = #{jobTitle , jdbcType=VARCHAR} or job_class = #{jobClass , jdbcType=VARCHAR} )
and job_title = #{jobTitle , jdbcType=VARCHAR}
and job_class = #{jobClass , jdbcType=VARCHAR}
